import React, {useState} from 'react';
import ReactDOM from 'react-dom';

const Statistic = ({text}) => {
  return (
    <>
      {/* <p>{text} {value}</p> */}
      <td>{text}</td>
    </>
  );
}

const Statistics = ({good, neutral, bad, total, average, percentage}) => {
  if(total !== 0){
    return (
      <div>
        <tr>
          <td>
            <h3>Statistics</h3>
          </td>
        </tr>
        <tr>
          <Statistic text="Good" />
          <Statistic text="Neutral" />
        </tr>
        <Statistic text={good} />
        <Statistic text={neutral} />
        <Statistic text="Bad" />
        <Statistic text={bad} />
        <Statistic text="Total" value={total} />
        <Statistic text="Average" value={average} />
        <Statistic text="Percentage" value={percentage + "%"} />
      </div>
    );
  } else {
    return (
      <>
        <h3>Statistics</h3>
        <p>No feedback given</p>
      </>
    );
  }
}

const App = () => {
  // save clicks of each button to own state
  const [good, setGood] = useState(0);
  const [neutral, setNeutral] = useState(0);
  const [bad, setBad] = useState(0);
  const [total, setTotal] = useState(0);
  const [average, setAverage] = useState(0);

  const handleGood = () => {
    setGood(good + 1);
    setTotal(total + 1);
    setAverage(average + 1);
  };
  const handleNeutral = () => {
    setNeutral(neutral + 1);
    setTotal(total + 1);
    setAverage(average + 0);
  };
  const handleBad = () => {
    setBad(bad + 1);
    setTotal(total + 1);
    setAverage(average - 1);
  };


  const Button = ({handleClick, text}) => {
    return (
      <button onClick={handleClick}>{text}</button>
    )
  }

  return (
    <div>
      <h3>Give Feedback</h3>

      <Button handleClick={handleGood} text="good" />
      <Button handleClick={handleNeutral} text="neutral" />
      <Button handleClick={handleBad} text="bad" />

      <Statistics
        good={good}
        neutral={neutral}
        bad={bad}
        total={total}
        average={average === 0 ? 0 : average / total}
        percentage={good === 0 ? 0 : (good / total) * 100}
      />

      {/* <h3>Statistics</h3>
      <p>Good {good}</p>
      <p>Neutral {neutral}</p>
      <p>Bad {bad}</p>
      <p>Total {total}</p>
      <p>Average {(average === 0) ? 0 : average / total}</p>
      <p>Positive {(good === 0) ? 0 : (good / total)*100 }%</p> */}
    </div>
  );
};

ReactDOM.render(<App />, document.getElementById("root"));

